@never_cache
def home(request):
    if request.method == 'GET':
        processed_image_base64 = ""
        buttonNumbeer = request.GET.get('buttonNumber')
        uploadedImagePath = request.GET.get('uploadedImage')

        return render(request, 'home.html', {'processed_image_base64': processed_image_base64})
    
    if request.method == 'POST' and request.FILES['image']:
        image_file = request.FILES['image']
        fs = FileSystemStorage()
        filename = fs.save(image_file.name, image_file)
        uploaded_image = fs.url(filename)  # Use FileSystemStorage to get the URL
        processed_image_base64 = ""
        return render(request, 'home.html', {'uploaded_image': uploaded_image, 'processed_image_base64': processed_image_base64})
    
